<?php	class Event extends AppModel	{		public $name = "Events";		public $users = "Users";        public $dog = "Dog";		public $belongsTo = array(        'User' => array(            'className'    => 'User',            'foreignKey'   => 'event_userid'        )    );        public $hasAndBelongsToMany = array(            'Dog'=>array(                'className' =>'Dog',                'joinTable' => 'dogs_events',                'foreignKey' => 'event_id',                'associationForeignKey' => 'dog_id',                'unique' => 'true')        );		var $validate = array(				'event_theme'=> array(					'mustNotEmpty'=>array(						'rule' => 'notEmpty',						'message'=> 'Please enter the event name'						),					'event_theme_valid'=>array(						'rule' => array('custom', '/^[a-z ]*$/i'),						'message'=> 'Only letters allowed.'),					),									'date'=> array(					'mustNotEmpty'=>array(						'rule' => 'notEmpty',						'message'=> 'Please choose the date')                )					);				}?>